package textadventure;import items.Container;/** * A Player can contain objects and knows the Room it's currently in. */public class Player extends Container {	private int health;	private boolean hasBrushedTeeth = false;	private boolean isWearingClothes = false;	/** Room this Player is in at the moment */	private Room currentRoom;		/** Creates a player that starts in the given Room with no items */	public Player(Room startRoom, World world, int health) {		super(world, "Player", "You are looking good today!");		currentRoom = startRoom;		health = 0;	}	/* Getters and setters */	public Room getCurrentRoom() {		return currentRoom;	}	public void setCurrentRoom(Room newRoom) {		currentRoom = newRoom;	}		public int getHealth() {		return health;	}		public void setHealth(int health) {		this.health = health;	}		public boolean getHasBrushedTeeth() {		return hasBrushedTeeth;	}		public void setHasBrushedTeeth(boolean hasBrushedTeeth) {		this.hasBrushedTeeth = false;	}	public boolean isWearingClothes() {		return isWearingClothes;	}	public void setWearingClothes(boolean isWearingClothes) {		this.isWearingClothes = isWearingClothes;	}		}